		$_REQUEST['option'] = 'afterforumids';
	}
}

global $do, $vbphrase, $vbulletin;
if ($do == 'index' AND $vbulletin->userinfo['permissions']['ptpermissions'] & $vbulletin->bf_ugp_ptpermissions['canviewprojecttools'])
{
	$return .= "<div style=\"clear: both\" align=\"center\"><a href=\"" . $vbulletin->options['bburl'] . "/archive/project.php\">$vbphrase[projects]</a></div>";
}

$pt_templates =  array(
	'pt_navbar_search',
	'pt_navbar_link',
);

switch (THIS_SCRIPT)
{
	case 'index':
	case 'forumdisplay':
		if ($vbulletin->options['pt_hasprojectforums'])
		{
			$pt_templates[] = 'pt_forumbit_project';
			$pt_templates[] = 'pt_markread_script';
			$pt_templates[] = 'pt_subforumbit_project';
			$pt_templates[] = 'pt_projectbit_typecount';
		}
		break;

	case 'usercp':
		$pt_templates[] = 'pt_issuebit';
		$pt_templates[] = 'pt_usercp_subscriptions';
		break;

	case 'member':
		$pt_templates[] = 'pt_member_findissues_37';
		break;
}

if (in_array('usercp_nav_folderbit', $cache))
{
	$pt_templates[] = 'pt_usercp_navbit';
}

$cache = array_merge($cache, $pt_templates);
unset($pt_templates);

$bits = array(
	'canpt' => 1,
	'canptpermissions' => 2
);

foreach($do AS $field)
{
	if (isset($bits["$field"]) AND ($admin['ptpermissions'] & $bits["$field"]))
	{
		$return_value = true;
		break;
	}
}

if ((!defined('VB_ERROR_LITE') OR VB_ERROR_LITE != true) AND defined('THIS_SCRIPT') AND substr(THIS_SCRIPT, 0, 7) == 'project')
{
	// we're in a project file, so make sure we include that in the navbar
	if ($vbulletin->userinfo['permissions']['forumpermissions'] & $vbulletin->bf_ugp_forumpermissions['canview'])
	{
		$navbits = construct_navbits(array(
			'project.php' . $vbulletin->session->vars['sessionurl_q'] => $vbphrase['projects'],
			'' => $vbphrase['vbulletin_message']
		));
		$navbar = render_navbar_template($navbits);
	}
}

global $phrasegroups;

switch (THIS_SCRIPT)
{
	case 'index':
	case 'forumdisplay':
		if ($vbulletin->options['pt_hasprojectforums'])
		{
			$phrasegroups[] = 'projecttools';

			$datastore_fetch[] = "'pt_bitfields'";
			$datastore_fetch[] = "'pt_permissions'";
			$datastore_fetch[] = "'pt_projects'";
		}
		break;

	case 'usercp':
	case 'member':
	case 'profile':
		$phrasegroups[] = 'projecttools';

		$datastore_fetch[] = "'pt_bitfields'";
		$datastore_fetch[] = "'pt_issuestatus'";
		$datastore_fetch[] = "'pt_issuetype'";
		$datastore_fetch[] = "'pt_permissions'";
		break;
}

if (strpos($loc, '/archive/project.php') !== false)
{
	$filename = 'archive_project';
	$endbit = substr(strrchr($token, '/') , 1);
	if ($endbit != '' AND $endbit != 'project.php')
	{
		$loc = 'archive_project?' . str_replace('-', '=', $endbit);
		$filename = strtok($loc, '?');
		$token = $filename;
	}
}

if ($usergroups_changed OR (isset($this->user['username']) AND $this->user['username'] != $this->existing['username']))
{
	require_once(DIR . '/includes/adminfunctions_projecttools.php');
	build_assignable_users();
	build_pt_user_list('pt_report_users', 'pt_report_user_cache');
}
