code.installcode:
// Set Product URLs //
$info['url'] = 'http://www.vbulletin.org/forum/misc.php?do=producthelp&pid='.$info['productid'];
$info['versioncheckurl'] = 'http://www.vbulletin.org/forum/misc.php?do=productcheck&pid='.$info['productid'];

if (!file_exists(DIR . '/includes/adminfunctions_projecttools.php') OR !file_exists(DIR . '/includes/xml/bitfield_vbprojecttools.xml'))
{
	print_dots_stop();
	print_cp_message('Please upload the files that came with the vBulletin Project Tools before installing or upgrading!');
}

define('DISABLE_PRODUCT_REDIRECT', true);
define('CP_REDIRECT', 'index.php?loc=' . urlencode('project.php?do=install&installed_version=' . urlencode($installed_version)));

$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issue ( issueid INT UNSIGNED AUTO_INCREMENT NOT NULL, projectid INT UNSIGNED NOT NULL DEFAULT 0, issuestatusid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', title VARCHAR(250) NOT NULL DEFAULT '', summary VARCHAR(250) NOT NULL DEFAULT '', submituserid INT UNSIGNED NOT NULL DEFAULT 0, submitusername VARCHAR(100) NOT NULL DEFAULT '', submitdate INT UNSIGNED NOT NULL DEFAULT 0, appliesversionid INT UNSIGNED NOT NULL DEFAULT 0, isaddressed SMALLINT UNSIGNED NOT NULL DEFAULT 0, addressedversionid INT UNSIGNED NOT NULL DEFAULT 0, priority SMALLINT UNSIGNED NOT NULL DEFAULT 1, visible ENUM('moderation', 'visible', 'private', 'deleted') NOT NULL DEFAULT 'visible', lastpost INT UNSIGNED NOT NULL DEFAULT 0, lastactivity INT UNSIGNED NOT NULL DEFAULT 0, lastpostuserid INT UNSIGNED NOT NULL DEFAULT 0, lastpostusername VARCHAR(100) NOT NULL DEFAULT '', firstnoteid INT UNSIGNED NOT NULL DEFAULT 0, lastnoteid INT UNSIGNED NOT NULL DEFAULT 0, attachcount INT UNSIGNED NOT NULL DEFAULT 0, pendingpetitions INT UNSIGNED NOT NULL DEFAULT 0, replycount INT UNSIGNED NOT NULL DEFAULT 0, votepositive INT UNSIGNED NOT NULL DEFAULT 0, votenegative INT UNSIGNED NOT NULL DEFAULT 0, projectcategoryid INT UNSIGNED NOT NULL DEFAULT 0, assignedusers INT UNSIGNED NOT NULL DEFAULT 0, privatecount INT UNSIGNED NOT NULL DEFAULT 0, state ENUM('open', 'closed') NOT NULL DEFAULT 'open', milestoneid INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid), KEY finder (projectid, visible, issuetypeid, lastpost), KEY issuestatusid (issuestatusid), FULLTEXT KEY textsearch (title, summary) ) ENGINE=MYISAM ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issueassign ( issueid INT UNSIGNED NOT NULL DEFAULT 0, userid INT UNSIGNED NOT NULL DEFAULT 0, dateline INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid, userid), KEY userid (userid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issueattach ( attachmentid INT UNSIGNED AUTO_INCREMENT NOT NULL, issueid INT UNSIGNED NOT NULL DEFAULT 0, userid INT UNSIGNED NOT NULL DEFAULT 0, filename VARCHAR(100) NOT NULL DEFAULT '', extension VARCHAR(20) NOT NULL DEFAULT '', dateline INT UNSIGNED NOT NULL DEFAULT 0, visible SMALLINT UNSIGNED NOT NULL DEFAULT 1, status ENUM('obsolete', 'current') NOT NULL DEFAULT 'current', filesize INT UNSIGNED NOT NULL DEFAULT 0, filehash VARCHAR(32) NOT NULL DEFAULT '', filedata MEDIUMBLOB NOT NULL, thumbnail MEDIUMBLOB NOT NULL, thumbnail_filesize INT UNSIGNED NOT NULL DEFAULT 0, thumbnail_dateline INT UNSIGNED NOT NULL DEFAULT 0, ispatchfile SMALLINT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (attachmentid), KEY finder (issueid, visible, dateline) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuechange ( issuechangeid INT UNSIGNED AUTO_INCREMENT NOT NULL, issueid INT UNSIGNED NOT NULL DEFAULT 0, userid INT UNSIGNED NOT NULL DEFAULT 0, dateline INT UNSIGNED NOT NULL DEFAULT 0, issuenoteid INT UNSIGNED NOT NULL DEFAULT 0, field VARCHAR(25) NOT NULL DEFAULT '', oldvalue TEXT NOT NULL, newvalue TEXT NOT NULL, PRIMARY KEY (issuechangeid), KEY finder (issueid, dateline) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuedeletionlog ( primaryid INT UNSIGNED NOT NULL DEFAULT 0, type ENUM('issue', 'issuenote') NOT NULL DEFAULT 'issue', userid INT UNSIGNED NOT NULL DEFAULT 0, username VARCHAR(100) NOT NULL DEFAULT '', reason VARCHAR(250) NOT NULL DEFAULT '', dateline INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (primaryid, type), KEY moderation (type, dateline) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuenote ( issuenoteid INT UNSIGNED AUTO_INCREMENT NOT NULL, issueid INT UNSIGNED NOT NULL DEFAULT 0, dateline INT UNSIGNED NOT NULL DEFAULT 0, pagetext TEXT NOT NULL, userid INT UNSIGNED NOT NULL DEFAULT 0, username VARCHAR(100) NOT NULL DEFAULT '', type ENUM('user', 'system', 'petition') NOT NULL DEFAULT 'user', ispending SMALLINT UNSIGNED NOT NULL DEFAULT 0, visible ENUM('moderation', 'visible', 'private', 'deleted') NOT NULL DEFAULT 'visible', lasteditdate INT UNSIGNED NOT NULL DEFAULT 0, isfirstnote SMALLINT UNSIGNED NOT NULL DEFAULT 0, ipaddress INT UNSIGNED NOT NULL DEFAULT 0, reportthreadid INT UNSIGNED NOT NULL DEFAULT '0', PRIMARY KEY (issuenoteid), KEY finder (issueid, visible, dateline), FULLTEXT KEY textsearch (pagetext) ) ENGINE=MYISAM ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuenotehash ( issuenoteid INT UNSIGNED NOT NULL, userid INT UNSIGNED NOT NULL, issueid INT UNSIGNED NOT NULL, dupehash CHAR(32) NOT NULL, dateline INT UNSIGNED NOT NULL, PRIMARY KEY (issuenoteid), KEY finder (dateline, userid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuenotehistory ( issuenotehistoryid INT UNSIGNED AUTO_INCREMENT NOT NULL, issuenoteid INT UNSIGNED NOT NULL DEFAULT 0, reason VARCHAR(250) NOT NULL, pagetext TEXT NOT NULL, visible ENUM('moderation', 'visible', 'private', 'deleted') NOT NULL DEFAULT 'visible', dateline INT UNSIGNED NOT NULL DEFAULT 0, userid INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issuenotehistoryid), KEY finder (issuenoteid, dateline) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuepetition ( issuenoteid INT UNSIGNED NOT NULL, petitionstatusid INT UNSIGNED NOT NULL DEFAULT 0, resolution ENUM('pending', 'accepted', 'rejected', 'cancelled') NOT NULL DEFAULT 'pending', resolveuserid INT UNSIGNED NOT NULL DEFAULT 0, resolvedate INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issuenoteid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issueprivatelastpost ( issueid INT UNSIGNED NOT NULL DEFAULT 0, lastnoteid INT UNSIGNED NOT NULL DEFAULT 0, lastpostuserid VARCHAR(100) NOT NULL, lastpostusername VARCHAR(100) NOT NULL, lastpost INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issueread ( userid INT UNSIGNED NOT NULL DEFAULT 0, issueid INT UNSIGNED NOT NULL DEFAULT 0, readtime INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid, userid), KEY readtime (readtime) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuereport ( issuereportid INT UNSIGNED AUTO_INCREMENT NOT NULL, title VARCHAR(100) NOT NULL DEFAULT '', description TEXT NOT NULL, public SMALLINT UNSIGNED NOT NULL DEFAULT 0, userid INT UNSIGNED NOT NULL DEFAULT 0, criteria TEXT NOT NULL, sortby VARCHAR(25) NOT NULL DEFAULT '', sortorder ENUM('asc', 'desc') NOT NULL DEFAULT 'desc', groupby VARCHAR(25) NOT NULL DEFAULT '', projectlist VARCHAR(250) NOT NULL DEFAULT '', issuetypelist VARCHAR(250) NOT NULL DEFAULT '', PRIMARY KEY (issuereportid), KEY title (title) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuereportsubscribe ( userid INT UNSIGNED NOT NULL DEFAULT 0, issuereportid INT UNSIGNED NOT NULL DEFAULT 0, issuesearchid INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (userid, issuereportid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuesearch ( issuesearchid INT UNSIGNED AUTO_INCREMENT NOT NULL, userid INT UNSIGNED NOT NULL DEFAULT 0, ipaddress VARCHAR(15) NOT NULL DEFAULT '', criteria TEXT NOT NULL, sortby VARCHAR(25) NOT NULL DEFAULT '', sortorder ENUM('asc', 'desc') NOT NULL DEFAULT 'asc', groupby VARCHAR(25) NOT NULL DEFAULT '', searchtime FLOAT UNSIGNED NOT NULL DEFAULT 0.0, resultcount SMALLINT UNSIGNED NOT NULL DEFAULT 0, dateline INT UNSIGNED NOT NULL DEFAULT 0, completed SMALLINT UNSIGNED NOT NULL DEFAULT 0, issuereportid INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issuesearchid), KEY userid (userid), KEY ipaddress (ipaddress) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuesearchresult ( issuesearchid INT UNSIGNED NOT NULL DEFAULT 0, issueid INT UNSIGNED NOT NULL DEFAULT 0, offset INT UNSIGNED NOT NULL DEFAULT 0, groupid VARCHAR(100) NOT NULL DEFAULT '', PRIMARY KEY (issuesearchid, offset) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuestatus ( issuestatusid INT UNSIGNED AUTO_INCREMENT NOT NULL, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', displayorder INT UNSIGNED NOT NULL DEFAULT 0, canpetitionfrom SMALLINT UNSIGNED NOT NULL DEFAULT 0, issuecompleted SMALLINT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issuestatusid), KEY finder (issuetypeid, displayorder) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuesubscribe ( userid INT UNSIGNED NOT NULL DEFAULT 0, issueid INT UNSIGNED NOT NULL DEFAULT 0, subscribetype ENUM('instant', 'daily', 'weekly', 'none') NOT NULL DEFAULT 'none', PRIMARY KEY (userid, issueid), KEY issueid (issueid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuetag ( issueid INT UNSIGNED NOT NULL DEFAULT 0, tagid INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid, tagid), KEY tagid (tagid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuetype ( issuetypeid VARCHAR(25) NOT NULL DEFAULT '', displayorder INT UNSIGNED NOT NULL DEFAULT 0, iconfile VARCHAR(250) NOT NULL DEFAULT '', PRIMARY KEY (issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issuevote ( issuevoteid INT UNSIGNED AUTO_INCREMENT NOT NULL, userid INT UNSIGNED NOT NULL DEFAULT 0, ipaddress VARCHAR(15) NOT NULL DEFAULT '', issueid INT UNSIGNED NOT NULL DEFAULT 0, dateline INT UNSIGNED NOT NULL DEFAULT 0, vote ENUM('positive', 'negative') NOT NULL DEFAULT 'positive', PRIMARY KEY (issuevoteid), KEY finder (issueid, vote) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_milestone ( milestoneid INT UNSIGNED NOT NULL AUTO_INCREMENT, title VARCHAR(100) NOT NULL DEFAULT '', title_clean VARCHAR(100) NOT NULL DEFAULT '', description MEDIUMTEXT, projectid INT UNSIGNED NOT NULL DEFAULT 0, targetdate INT NOT NULL DEFAULT 0, completeddate INT NOT NULL DEFAULT 0, PRIMARY KEY (milestoneid), INDEX (projectid, completeddate, targetdate) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_milestonetypecount ( milestoneid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', activepublic INT UNSIGNED NOT NULL DEFAULT 0, activeprivate INT UNSIGNED NOT NULL DEFAULT 0, completepublic INT UNSIGNED NOT NULL DEFAULT 0, completeprivate INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (milestoneid, issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_project ( projectid INT UNSIGNED AUTO_INCREMENT NOT NULL, displayorder INT UNSIGNED NOT NULL DEFAULT 0, title VARCHAR(250) NOT NULL DEFAULT '', title_clean VARCHAR(250) NOT NULL DEFAULT '', summary TEXT NOT NULL, summary_clean TEXT NOT NULL, description TEXT NOT NULL, options INT UNSIGNED NOT NULL DEFAULT 0, afterforumids BLOB NOT NULL, forumtitle VARCHAR(250) NOT NULL DEFAULT '', milestonecount INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (projectid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projectcategory ( projectcategoryid INT UNSIGNED AUTO_INCREMENT NOT NULL, projectid INT UNSIGNED NOT NULL DEFAULT 0, title VARCHAR(250) NOT NULL DEFAULT '', displayorder INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (projectcategoryid), KEY finder (projectid, displayorder) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projectpermission ( usergroupid INT UNSIGNED NOT NULL DEFAULT 0, projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', generalpermissions INT UNSIGNED NOT NULL DEFAULT 0, postpermissions INT UNSIGNED NOT NULL DEFAULT 0, attachpermissions INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (usergroupid, projectid, issuetypeid), KEY projectid (projectid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projectread ( userid INT UNSIGNED NOT NULL DEFAULT 0, projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', readtime INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (projectid,userid, issuetypeid), KEY readtime (readtime) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projecttype ( projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', startstatusid INT UNSIGNED NOT NULL DEFAULT 0, issuecount INT UNSIGNED NOT NULL DEFAULT 0, issuecountactive INT UNSIGNED NOT NULL DEFAULT 0, lastactivity INT UNSIGNED NOT NULL DEFAULT 0, lastpost INT UNSIGNED NOT NULL DEFAULT 0, lastpostuserid INT UNSIGNED NOT NULL DEFAULT 0, lastpostusername VARCHAR(100) NOT NULL DEFAULT 0, lastpostid INT UNSIGNED NOT NULL DEFAULT 0, lastissueid INT UNSIGNED NOT NULL DEFAULT 0, lastissuetitle VARCHAR(250) NOT NULL DEFAULT '', PRIMARY KEY (projectid, issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projecttypeprivatelastpost ( projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', lastpost INT UNSIGNED NOT NULL DEFAULT 0, lastpostuserid INT UNSIGNED NOT NULL DEFAULT 0, lastpostusername VARCHAR(100) NOT NULL DEFAULT 0, lastpostid INT UNSIGNED NOT NULL DEFAULT 0, lastissueid INT UNSIGNED NOT NULL DEFAULT 0, lastissuetitle VARCHAR(250) NOT NULL DEFAULT '', PRIMARY KEY (projectid, issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projecttypesubscribe ( userid INT UNSIGNED NOT NULL DEFAULT 0, projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', subscribetype ENUM('daily','weekly','none') NOT NULL DEFAULT 'none', PRIMARY KEY (userid, projectid, issuetypeid), KEY projectid (projectid, issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projectversion ( projectversionid INT UNSIGNED AUTO_INCREMENT NOT NULL, projectid INT UNSIGNED NOT NULL DEFAULT 0, versionname VARCHAR(50) NOT NULL DEFAULT '', projectversiongroupid INT UNSIGNED NOT NULL DEFAULT 0, displayorder SMALLINT UNSIGNED NOT NULL DEFAULT 0, effectiveorder INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (projectversionid), KEY finder (projectid, effectiveorder) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projectversiongroup ( projectversiongroupid INT UNSIGNED AUTO_INCREMENT NOT NULL, projectid INT UNSIGNED NOT NULL DEFAULT 0, groupname VARCHAR(50) NOT NULL DEFAULT '', displayorder SMALLINT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (projectversiongroupid), KEY finder (projectid, displayorder) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_tag ( tagid INT UNSIGNED AUTO_INCREMENT NOT NULL, tagtext VARCHAR(50) NOT NULL DEFAULT '', PRIMARY KEY (tagid), UNIQUE KEY tagtext (tagtext) ) ");
require_once(DIR . '/includes/class_dbalter.php'); $db_alter = new vB_Database_Alter_MySQL($db); if ($db_alter->fetch_table_info('administrator')) { $db_alter->add_field(array( 'name' => 'ptpermissions', 'type' => 'int', 'attributes' => 'unsigned' )); } if ($db_alter->fetch_table_info('usergroup')) { $db_alter->add_field(array( 'name' => 'ptpermissions', 'type' => 'int', 'attributes' => 'unsigned' )); }
// fresh install - give the installer full permissions if ($installed_version === null) { $userid = isset($vbulletin->userinfo['userid']) ? $vbulletin->userinfo['userid'] : null; setup_default_admin_permissions('ptpermissions', 3, $userid);
// Below this needs some translating!
// setup a default project ... $projectdata =& datamanager_init('Pt_Project', $vbulletin, ERRTYPE_SILENT); $projectdata->set('title', 'Default Project'); $projectdata->set('summary', 'This is a short summary of the project.'); $projectdata->set('description', 'This is a description of the project. You may wish to discuss things like goals, timelines, and responsibilities here.'); $projectdata->set('displayorder', 1); $projectid = $projectdata->save();
// ... and some default issue types ... $types = array( 'bug' => array( 'data' => array( 'displayorder' => 10, 'iconfile' => 'pt_type_bug.png', ), 'info' => array( 'title_singular' => 'Bug', 'title_plural' => 'Bugs', 'vote_question' => 'Are you able to reproduce this bug?', 'vote_count_positive' => 'Users able to reproduce bug', 'vote_count_negative' => 'Users unable to reproduce bug', 'applies_version' => 'Affected Version', 'addressed_version' => 'Fixed Version', 'post_new_issue' => 'Post New Bug', ) ),
'feature' => array( 'data' => array( 'displayorder' => 20, 'iconfile' => 'pt_type_feature.png', ), 'info' => array( 'title_singular' => 'Feature', 'title_plural' => 'Features', 'vote_question' => 'Would you like to see this feature implemented?', 'vote_count_positive' => 'Votes for this feature', 'vote_count_negative' => 'Votes against this feature', 'applies_version' => 'Suggested Version', 'addressed_version' => 'Implemented Version', 'post_new_issue' => 'Post New Feature', ) ),
'task' => array( 'data' => array( 'displayorder' => 30, 'iconfile' => 'pt_type_task.png', ), 'info' => array( 'title_singular' => 'Task', 'title_plural' => 'Tasks', 'vote_question' => 'Would you like to see this task performed?', 'vote_count_positive' => 'Votes to perform', 'vote_count_negative' => 'Votes not to perform', 'applies_version' => 'Target Version', 'addressed_version' => 'Completed Version', 'post_new_issue' => 'Post New Task', ) ) ); $typeids = array();
foreach ($types AS $issuetypeid => $fields) { $dm =& datamanager_init('Pt_IssueType', $vbulletin, ERRTYPE_SILENT); $dm->set('issuetypeid', $issuetypeid); $dm->set_info('rebuild_caches', false);
foreach ($fields['data'] AS $key => $val) { $dm->set($key, $val); } foreach ($fields['info'] AS $key => $val) { $dm->set_info($key, $val); }
$dm->save(); }
// ... and some statuses $statuses = array( 'bug' => array( 'Unconfirmed' => array('displayorder' => 10, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'Awaiting Feedback' => array('displayorder' => 20, 'canpetitionfrom' => 1, 'issuecompleted' => 0), 'Cannot Reproduce' => array('displayorder' => 30, 'canpetitionfrom' => 1, 'issuecompleted' => 0), 'Confirmed' => array('displayorder' => 40, 'canpetitionfrom' => 1, 'issuecompleted' => 0), 'Duplicate' => array('displayorder' => 50, 'canpetitionfrom' => 0, 'issuecompleted' => 1), 'Not a Bug' => array('displayorder' => 60, 'canpetitionfrom' => 0, 'issuecompleted' => 1), 'Fixed' => array('displayorder' => 100, 'canpetitionfrom' => 1, 'issuecompleted' => 1), 'No Response' => array('displayorder' => 110, 'canpetitionfrom' => 1, 'issuecompleted' => 1), ), 'feature' => array( 'Suggested' => array('displayorder' => 10, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'Accepted' => array('displayorder' => 20, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'In Progress' => array('displayorder' => 30, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'Implemented' => array('displayorder' => 100, 'canpetitionfrom' => 1, 'issuecompleted' => 1), 'Rejected' => array('displayorder' => 110, 'canpetitionfrom' => 0, 'issuecompleted' => 1), 'On Hold' => array('displayorder' => 120, 'canpetitionfrom' => 0, 'issuecompleted' => 0), ), 'task' => array( 'Requested' => array('displayorder' => 10, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'Accepted' => array('displayorder' => 20, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'In Progress' => array('displayorder' => 30, 'canpetitionfrom' => 0, 'issuecompleted' => 0), 'Completed' => array('displayorder' => 100, 'canpetitionfrom' => 1, 'issuecompleted' => 1), 'Rejected' => array('displayorder' => 110, 'canpetitionfrom' => 0, 'issuecompleted' => 1), ) ); $statusids = array();
foreach ($statuses AS $issuetypeid => $typestatuses) { foreach ($typestatuses AS $statustitle => $fields) { $dm =& datamanager_init('Pt_IssueStatus', $vbulletin, ERRTYPE_SILENT); $dm->set('issuetypeid', $issuetypeid); $dm->set_info('title', $statustitle); $dm->set_info('rebuild_caches', false);
foreach ($fields AS $key => $val) { $dm->set($key, $val); }
$statusids["$issuetypeid"]["$statustitle"] = $dm->save(); } }
if ($projectid) { $db->query_write(" INSERT IGNORE INTO " . TABLE_PREFIX . "pt_projecttype (projectid, issuetypeid, startstatusid) VALUES ($projectid, 'bug', " . intval($statusids['bug']['Unconfirmed']) . "), ($projectid, 'feature', " . intval($statusids['feature']['Suggested']) . "), ($projectid, 'task', " . intval($statusids['task']['Requested']) . ") "); }
// now lets setup decent user group permissions. Come up with a default set to apply // given the group's specific settings: // Admins and smods = adminmod; new thread posters = poster; can view = viewer $perm_set = array( 'adminmod' => array( 'generalpermissions' => 122879, 'postpermissions' => 65535, 'attachpermissions' => 15, 'ug_ptpermissions' => 63 ), 'poster' => array( 'generalpermissions' => 81971, 'postpermissions' => 4383, 'attachpermissions' => 7, 'ug_ptpermissions' => 17 ), 'viewer' => array( 'generalpermissions' => 65555, 'postpermissions' => 0, 'attachpermissions' => 1, 'ug_ptpermissions' => 16 ), 'none' => array( 'generalpermissions' => 0, 'postpermissions' => 0, 'attachpermissions' => 0, 'ug_ptpermissions' => 0 ) );
$usergroups = $db->query_read(" SELECT * FROM " . TABLE_PREFIX . "usergroup "); while ($usergroup = $db->fetch_array($usergroups)) { // 4 default groups should never be considered admins/mods here $force_nonadmin = in_array($usergroup['usergroupid'], array(1, 2, 3, 4));
if (!$force_nonadmin AND intval($usergroup['adminpermissions']) & $vbulletin->bf_ugp_adminpermissions['cancontrolpanel']) { // admin $selected_set = $perm_set['adminmod']; } else if (!$force_nonadmin AND intval($usergroup['adminpermissions']) & $vbulletin->bf_ugp_adminpermissions['ismoderator']) { // super mod $selected_set = $perm_set['adminmod']; } else if (intval($usergroup['forumpermissions']) & $vbulletin->bf_ugp_forumpermissions['canpostnew']) { // poster $selected_set = $perm_set['poster']; } else if (intval($usergroup['forumpermissions']) & $vbulletin->bf_ugp_forumpermissions['canview']) { // viewer $selected_set = $perm_set['viewer']; } else { // no perms $selected_set = $perm_set['none']; }
$perms = array(); foreach (array_keys($types) AS $issuetypeid) { $perms[] = "($usergroup[usergroupid], 0, '$issuetypeid', $selected_set[generalpermissions], $selected_set[postpermissions], $selected_set[attachpermissions])"; }
// insert some good permissions for the admin group $db->query_write(" INSERT INTO " . TABLE_PREFIX . "pt_projectpermission (usergroupid, projectid, issuetypeid, generalpermissions, postpermissions, attachpermissions) VALUES " . implode(',', $perms) );
$db->query_write(" UPDATE " . TABLE_PREFIX . "usergroup SET ptpermissions = $selected_set[ug_ptpermissions] WHERE usergroupid = $usergroup[usergroupid] "); }
build_forum_permissions(); }
require_once(DIR . '/includes/adminfunctions_projecttools.php'); build_issue_type_cache(); build_assignable_users(); // builds perms and bitfields as well build_pt_user_list('pt_report_users', 'pt_report_user_cache'); build_version_cache(); build_project_cache();

