code.installcode:
if ($installed_version !== null)
{
	$db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_project ADD afterforumids BLOB NOT NULL");
	$db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_project ADD forumtitle VARCHAR(250) NOT NULL");
}

if ($installed_version !== null) { $db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_issue ADD privatecount INT UNSIGNED NOT NULL DEFAULT 0, ADD milestoneid INT UNSIGNED NOT NULL DEFAULT 0, ADD state ENUM('open', 'closed') NOT NULL DEFAULT 'open' ");
$db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_issuenote ADD reportthreadid INT UNSIGNED NOT NULL DEFAULT 0 ");
$db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_issuestatus ADD issuecompleted SMALLINT UNSIGNED NOT NULL DEFAULT 0");
$db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_project ADD milestonecount INT UNSIGNED NOT NULL DEFAULT 0");
$db->query_write("ALTER TABLE " . TABLE_PREFIX . "pt_projecttype ADD issuecountactive INT UNSIGNED NOT NULL DEFAULT 0, ADD lastpost INT UNSIGNED NOT NULL DEFAULT 0, ADD lastpostuserid INT UNSIGNED NOT NULL DEFAULT 0, ADD lastpostusername VARCHAR(100) NOT NULL DEFAULT 0, ADD lastpostid INT UNSIGNED NOT NULL DEFAULT 0, ADD lastissueid INT UNSIGNED NOT NULL DEFAULT 0, ADD lastissuetitle VARCHAR(250) NOT NULL DEFAULT '' ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issueread ( userid INT UNSIGNED NOT NULL DEFAULT 0, issueid INT UNSIGNED NOT NULL DEFAULT 0, readtime INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid, userid), KEY readtime (readtime) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_issueprivatelastpost ( issueid INT UNSIGNED NOT NULL DEFAULT 0, lastnoteid INT UNSIGNED NOT NULL DEFAULT 0, lastpostuserid VARCHAR(100) NOT NULL, lastpostusername VARCHAR(100) NOT NULL, lastpost INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (issueid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_milestone ( milestoneid INT UNSIGNED NOT NULL AUTO_INCREMENT, title VARCHAR(100) NOT NULL DEFAULT '', title_clean VARCHAR(100) NOT NULL DEFAULT '', description MEDIUMTEXT, projectid INT UNSIGNED NOT NULL DEFAULT 0, targetdate INT NOT NULL DEFAULT 0, completeddate INT NOT NULL DEFAULT 0, PRIMARY KEY (milestoneid), INDEX (projectid, completeddate, targetdate) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_milestonetypecount ( milestoneid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', activepublic INT UNSIGNED NOT NULL DEFAULT 0, activeprivate INT UNSIGNED NOT NULL DEFAULT 0, completepublic INT UNSIGNED NOT NULL DEFAULT 0, completeprivate INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (milestoneid, issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projectread ( userid INT UNSIGNED NOT NULL DEFAULT 0, projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', readtime INT UNSIGNED NOT NULL DEFAULT 0, PRIMARY KEY (projectid,userid, issuetypeid), KEY readtime (readtime) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projecttypeprivatelastpost ( projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', lastpost INT UNSIGNED NOT NULL DEFAULT 0, lastpostuserid INT UNSIGNED NOT NULL DEFAULT 0, lastpostusername VARCHAR(100) NOT NULL DEFAULT 0, lastpostid INT UNSIGNED NOT NULL DEFAULT 0, lastissueid INT UNSIGNED NOT NULL DEFAULT 0, lastissuetitle VARCHAR(250) NOT NULL DEFAULT '', PRIMARY KEY (projectid, issuetypeid) ) ");
$db->query_write(" CREATE TABLE " . TABLE_PREFIX . "pt_projecttypesubscribe ( userid INT UNSIGNED NOT NULL DEFAULT 0, projectid INT UNSIGNED NOT NULL DEFAULT 0, issuetypeid VARCHAR(25) NOT NULL DEFAULT '', subscribetype ENUM('daily','weekly','none') NOT NULL DEFAULT 'none', PRIMARY KEY (userid, projectid, issuetypeid), KEY projectid (projectid, issuetypeid) ) ");
require_once(DIR . '/includes/adminfunctions_projecttools.php'); build_pt_user_list('pt_report_users', 'pt_report_user_cache');
// general: canviewothers -> canviewmilestones, canmanage -> canmoveissue // post: caneditissueothers -> canchangemilestone, caneditissueothers -> cancloseissue $db->query_write(" UPDATE " . TABLE_PREFIX . "pt_projectpermission SET generalpermissions = generalpermissions | IF(generalpermissions & 2, 65536, 0) | IF(generalpermissions & 8, 32768, 0), postpermissions = postpermissions | IF(postpermissions & 32, 32768, 0) | IF(postpermissions & 32, 16384, 0) ");
$status_complete = array( 'bug' => array( 'Unconfirmed' => 0, 'Awaiting Feedback' => 0, 'Cannot Reproduce' => 0, 'Confirmed' => 0, 'Duplicate' => 1, 'Not a Bug' => 1, 'Fixed' => 1, 'No Response' => 1, ), 'feature' => array( 'Suggested' => 0, 'Accepted' => 0, 'In Progress' => 0, 'Implemented' => 1, 'Rejected' => 1, 'On Hold' => 0, ), 'task' => array( 'Requested' => 0, 'Accepted' => 0, 'In Progress' => 0, 'Completed' => 1, 'Rejected' => 1, ) );
$status_sql = $db->query_read(" SELECT issuestatus.issuestatusid, issuestatus.issuetypeid, phrase.text FROM " . TABLE_PREFIX . "pt_issuestatus AS issuestatus INNER JOIN " . TABLE_PREFIX . "phrase AS phrase ON (phrase.fieldname = 'projecttools' AND phrase.languageid = 0 AND phrase.varname = CONCAT('issuestatus', issuestatus.issuestatusid)) WHERE issuestatus.issuetypeid IN ('bug', 'feature', 'task') "); while ($status = $db->fetch_array($status_sql)) { if (isset($status_complete["$status[issuetypeid]"]["$status[text]"])) { $db->query_write(" UPDATE " . TABLE_PREFIX . "pt_issuestatus SET issuecompleted = " . intval($status_complete["$status[issuetypeid]"]["$status[text]"]) . " WHERE issuestatusid = $status[issuestatusid] "); } }
build_issue_type_cache(); build_project_permissions();
rebuild_project_counters(false); }

//Now the content type and related data so we can do searches.
$db->query_write("
	REPLACE INTO " . TABLE_PREFIX . "package
		(productid, class)
	VALUES
		('vbprojecttools', 'vBProjectTools')
");
$packageid = $db->insert_id();

$db->query_write("
	REPLACE INTO " . TABLE_PREFIX . "contenttype
		(class, packageid, canplace, cansearch, cantag, canattach)
	VALUES
		('Project', $packageid, '0', '0', '0', '0');
");

$db->query_write("
	REPLACE INTO " . TABLE_PREFIX . "contenttype
		(class, packageid, canplace, cansearch, cantag, canattach)
	VALUES
		('Issue', $packageid, '0', '0', '0', '0');
");

$show['ptimpoter_previous'] = false;

// The pt_importer product is present? Productid = 'db_ptimporter'
if ($ptimporter_product = $db->query_first("SELECT productid FROM " . TABLE_PREFIX . "product WHERE productid = 'db_ptimporter'"))
{
	$show['ptimporter_previous'] = true;
}

require_once(DIR . '/includes/class_dbalter.php');
$db_alter = new vB_Database_Alter_MySQL($db);
if ($db_alter->fetch_table_info('thread'))
{
	if (!$show['ptimporter_previous'])
	{
		// Product not found - do the original job
		$db_alter->add_field(array(
			'name' => 'pt_issueid',
			'type' => 'int',
			'attributes' => 'unsigned'
		));
		$db_alter->add_field(array(
			'name' => 'pt_forwardmode',
			'type' => 'int',
			'attributes' => 'unsigned'
		));
	}
	else
	{
		// Previous product found - do some specific job

		// We need to keep values of ptissueid and ptforwardmode into pt_issueid & pt_forwardmode
		// And create back the same original two fields to avoid errors on forced uninstall product
		$db->query_write("ALTER TABLE " . TABLE_PREFIX . "thread CHANGE ptissueid pt_issueid INT(10) UNSIGNED NOT NULL");
		$db->query_write("ALTER TABLE " . TABLE_PREFIX . "thread CHANGE ptforwardmode pt_forwardmode INT(10) UNSIGNED NOT NULL");

		$db_alter->add_field(array(
			'name' => 'ptissueid',
			'type' => 'int',
			'attributes' => 'unsigned'
		));
		$db_alter->add_field(array(
			'name' => 'ptforwardmode',
			'type' => 'int',
			'attributes' => 'unsigned'
		));
	}
}

// Uninstall the previous product
// There is no one function to do all the work on vB4, look at lines 1500-1600 of admincp/plugin.php
if ($show['ptimporter_previous'])
{
	// run uninstall code first; try to undo things in the opposite order they were done
	$productcodes = $db->query_read("
		SELECT version, uninstallcode
		FROM " . TABLE_PREFIX . "productcode
		WHERE productid = 'db_ptimporter'
			AND uninstallcode <> ''
	");

	$productcodes_grouped = array();
	$productcodes_versions = array();

	while ($productcode = $db->fetch_array($productcodes))
	{
		// have to be careful here, as version numbers are not necessarily unique
		$productcodes_versions["$productcode[version]"] = 1;
		$productcodes_grouped["$productcode[version]"][] = $productcode;
	}

	unset($productcodes_versions['*']);
	$productcodes_versions = array_keys($productcodes_versions);
	usort($productcodes_versions, 'version_sort');
	$productcodes_versions = array_reverse($productcodes_versions);

	if (!empty($productcodes_grouped['*']))
	{
		// run * entries first
		foreach ($productcodes_grouped['*'] AS $productcode)
		{
			eval($productcode['uninstallcode']);
		}
	}

	foreach ($productcodes_versions AS $version)
	{
		foreach ($productcodes_grouped["$version"] AS $productcode)
		{
			eval($productcode['uninstallcode']);
		}
	}

	//remove some common resources that a product may have registered.
	//tags
	$db->query_write("
		DELETE tagcontent
		FROM " . TABLE_PREFIX . "package AS package JOIN
			" . TABLE_PREFIX . "contenttype AS contenttype ON
				contenttype.packageid = package.packageid JOIN
			" . TABLE_PREFIX . "tagcontent AS tagcontent ON
				contenttype.contenttypeid = tagcontent.contenttypeid
		WHERE productid = 'db_ptimporter'
	");

	// Packages, routes, actions, contenttypes
	$db->query_write("
		DELETE package, route, action, contenttype
		FROM " . TABLE_PREFIX . "package AS package
		LEFT JOIN " . TABLE_PREFIX . "route AS route
			ON route.packageid = package.packageid
		LEFT JOIN " . TABLE_PREFIX . "action AS action
			ON action.routeid = route.routeid
		LEFT JOIN " . TABLE_PREFIX . "contenttype AS contenttype
			ON contenttype.packageid = package.packageid
		WHERE productid = 'db_ptimporter'
	");

	// Clear routes from datastore
	build_datastore('routes', serialize(array()), 1);

	//clear the type cache.
	vB_Cache::instance()->purge('vb_types.types');

	if ($db_alter->fetch_table_info('language'))
	{
		$phrasetypes = $db->query_read("
			SELECT fieldname
			FROM " . TABLE_PREFIX . "phrasetype
			WHERE product = 'db_ptimporter'
		");
		while ($phrasetype = $db->fetch_array($phrasetypes))
		{
			$db_alter->drop_field("phrasegroup_$phrasetype[fieldname]");
		}
	}

	// Delete latest product informations
	delete_product('db_ptimporter');

	// Ensure all will be rebuilt
	$rebuild['templates'] = true;
	$rebuild['phrases'] = true;
	$rebuild['options'] = true;
	$rebuild['cron'] = true;
}

$db->query_write("
	CREATE TABLE " . TABLE_PREFIX . "pt_issuestatusprojectset (
		issuestatusid INT UNSIGNED NOT NULL DEFAULT 0,
		projectid INT UNSIGNED NOT NULL DEFAULT 0,
		PRIMARY KEY (issuestatusid, projectid)
	)
");

$db->query_write("
	CREATE TABLE " . TABLE_PREFIX . "pt_user (
		userid INT UNSIGNED NOT NULL,
		totalissues INT UNSIGNED NOT NULL DEFAULT 0,
		PRIMARY KEY (userid)
	) ENGINE=MyISAM
");

$db->query_write("
	CREATE TABLE " . TABLE_PREFIX . "pt_issueimport (
		contenttypeid INT UNSIGNED NOT NULL,
		contentid INT UNSIGNED NOT NULL,
		data MEDIUMTEXT,
		UNIQUE KEY contenttypeid (contenttypeid, contentid)
	) ENGINE=MyISAM
");

require_once(DIR . '/includes/class_dbalter.php');
$db_alter = new vB_Database_Alter_MySQL($db);

// Add field for 'PT color titles by status' feature
if ($db_alter->fetch_table_info('pt_issuestatus'))
{
	$db_alter->add_field(array(
		'name' => 'statuscolor',
		'type' => 'varchar',
		'length' => '20',
		'null' => false
	));
}

// ############################################################
// Drop fields for 'Create issue from thread' feature - moved into a new table
// ############################################################

// Get the contenttypeid of threads
require_once(DIR . '/includes/class_bootstrap_framework.php');
vB_Bootstrap_Framework::init();
$contenttypeid = vB_Types::instance()->getContentTypeID('vBForum_Thread');

// Move any existing data into the new table before removing useless fields
$movedthreads = $db->query_read("
	SELECT threadid, ptissueid, ptforwardmode
	FROM " . TABLE_PREFIX . "thread
	WHERE ptissueid > 0
");

if ($db->num_rows($movedthreads) > 0)
{
	while ($movedthread = $db->fetch_array($movedthreads))
	{
		$data = array();
		$data['pt_issueid'] = $movedthread['ptissueid'];
		$data['pt_forwardmode'] = $movedthread['ptforwardmode'];

		$importdata = serialize($data);

		$db->query_write("
			INSERT INTO " . TABLE_PREFIX . "pt_issueimport
				(contenttypeid, contentid, data)
		VALUES
				($contenttypeid, " . $movedthread['threadid'] . ", '" . $importdata . "')
			");
	}
}

// Now remove useless fields
if ($db_alter->fetch_table_info('thread'))
{
	$db_alter->drop_field('ptissueid');
	$db_alter->drop_field('ptforwardmode');

	// These fields could be present even if the code is ran correctly - only a workaround here
	$db_alter->drop_field('pt_issueid');
	$db_alter->drop_field('pt_forwardmode');
}

// Add every user into pt_user table
$usercounts = $db->query_read("
	SELECT userid
	FROM " . TABLE_PREFIX . "user
	ORDER BY userid ASC
");

while ($usercount = $db->fetch_array($usercounts))
{
	$db->query_write("INSERT INTO " . TABLE_PREFIX . "pt_user (userid) VALUES (" . intval($usercount['userid']) . ")");
}

// Rebuild profile issue counters for every user
require_once(DIR . '/includes/adminfunctions_projecttools.php');
rebuild_profile_issue_counters();

code.uninstallcode:
require_once(DIR . '/includes/class_dbalter.php');
$db_alter = new vB_Database_Alter_MySQL($db);
if ($db_alter->fetch_table_info('pt_issuestatus'))
{
	$db_alter->drop_field('statuscolor');
}

$db->query_write("DROP TABLE IF EXISTS " . TABLE_PREFIX . "pt_issuestatusprojectset");

$db->query_write("DROP TABLE IF EXISTS " . TABLE_PREFIX . "pt_user");

$db->query_write("DROP TABLE IF EXISTS " . TABLE_PREFIX . "pt_issueimport");

