$issuedata = $db->query_first("
	SELECT data
	FROM " . TABLE_PREFIX . "pt_issueimport
	WHERE contenttypeid = $contenttypeid
		AND contentid = $thread[threadid]
");

$issueinfos = unserialize($issuedata['data']);

if (intval($issueinfos['pt_issueid']) > 0 AND $issueinfos['pt_forwardmode'] == 1)
{
	exec_header_redirect('project.php?' . $vbulletin->session->vars['sessionurl_js'] . "issueid=$issueinfos[pt_issueid]");
}

// Get the contenttypeid
$contenttypeid = vB_Types::instance()->getContentTypeID('vBForum_Post');

$hook_query_fields .= ', pt_issueimport.data AS pt_issueimport';
$hook_query_joins .= "LEFT JOIN " . TABLE_PREFIX . "pt_issueimport AS pt_issueimport ON (pt_issueimport.contentid = post.postid AND pt_issueimport.contenttypeid = $contenttypeid)";

$issueinfos = unserialize($thread['pt_issueimport']);

if (intval($issueinfos['pt_issueid']) > 0 AND $issueinfos['pt_forwardmode'] == 1)
{
	if ($vbulletin->options['ptimporter_extendedthreadprefix'])
	{
		$ptissueinfo = $db->query_first_slave("
			SELECT
				pt_issue.issuetypeid AS issuetype,
				pt_project.title AS project
			FROM " . TABLE_PREFIX . "pt_issue AS pt_issue
				LEFT JOIN " . TABLE_PREFIX . "pt_project AS pt_project ON (pt_issue.projectid = pt_project.projectid)
			WHERE pt_issue.issueid = " . $issueinfos['pt_issueid'] . "
		");

		$thread['typeprefix'] = construct_phrase($vbphrase['pt_import_threadprefix_extended'], $ptissueinfo['project'], $vbphrase["issuetype_$ptissueinfo[issuetype]_singular"]);
	}
	else
	{
		$thread['typeprefix'] = $vbphrase['pt_import_threadprefix'];
	}
	
	$thread['preview'] = '';
	$thread['views'] = '-';
	$thread['openclose_editable'] = '';
	$thread['statusicon'] = '_moved';
	$thread['pagenav'] = '';
	$thread['pagenav'] = '';
	$thread['rating'] = 0;
	$thread['votenum'] = 0;
	$thread['pagenav'] = '';
	$thread['showpagenav'] = false;
	$show['paperclip'] = false;
	$show['threadmoved'] = true;
	$show['threadicon'] = false;
	$show['threadcount'] = false;
	$show['sticky'] = false;
	$show['gotonewpost'] = false;
	$show['subscribed'] = false;
}

// Rebuild PT Assignable
if ($usergroups_changed OR (isset($this->user['username']) AND $this->user['username'] != $this->existing['username']))
{
	require_once(DIR . '/includes/adminfunctions_projecttools.php');
	build_assignable_users();
	build_pt_user_list('pt_report_users', 'pt_report_user_cache');
}

// Insert the new user into pt_user table
if (!$this->condition)
{
	$this->registry->db->query_write("
		INSERT INTO " . TABLE_PREFIX . "pt_user
			(userid)
		VALUES
			($userid)
	");
}
